import React, { useState } from 'react';

const MusicPlayer = () => {
  const [currentSong, setCurrentSong] = useState(null);

  const handleFileChange = (event) => {
    setCurrentSong(URL.createObjectURL(event.target.files[0]));
  };

  return (
    <div className="flex flex-col items-center justify-center h-full p-6">
      <input type="file" accept="audio/*" onChange={handleFileChange} className="mb-4 p-2 border border-gray-300 rounded" />
      {currentSong && <audio controls src={currentSong} className="w-full max-w-md" />}
    </div>
  );
};

export default MusicPlayer;
