import React, { useState } from 'react';

const MusicPlayer = () => {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [currentFileIndex, setCurrentFileIndex] = useState(null);

  const handleFileChange = (event) => {
    const files = Array.from(event.target.files);
    const newFiles = files.map((file) => ({
      url: URL.createObjectURL(file),
      name: file.name,
      albumArt: `${process.env.PUBLIC_URL}/default.png`, // Use relative URL for the default album art
    }));
    setSelectedFiles([...selectedFiles, ...newFiles]);
  };

  const handleRemoveFile = (index) => {
    const updatedFiles = selectedFiles.filter((_, i) => i !== index);
    setSelectedFiles(updatedFiles);
    if (index === currentFileIndex) {
      setCurrentFileIndex(null);
    }
  };

  const handlePlayFile = (index) => {
    setCurrentFileIndex(index);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="w-full max-w-md bg-white rounded-lg shadow-md p-6">
        <input
          type="file"
          accept="audio/*"
          multiple
          onChange={handleFileChange}
          className="mb-4 p-2 border border-gray-300 rounded"
        />
        {selectedFiles.length > 0 && (
          <ul className="list-disc mb-4">
            {selectedFiles.map((file, index) => (
              <li key={index} className="flex justify-between items-center mb-2">
                <span className="flex-grow cursor-pointer" onClick={() => handlePlayFile(index)}>
                  {file.name}
                </span>
                <button
                  onClick={() => handlePlayFile(index)}
                  className="ml-2 text-green-500 hover:text-green-700"
                >
                  Play
                </button>
                <button
                  onClick={() => handleRemoveFile(index)}
                  className="ml-2 text-red-500 hover:text-red-700"
                >
                  Remove
                </button>
              </li>
            ))}
          </ul>
        )}
        {currentFileIndex !== null && (
          <>
            <img
              src={selectedFiles[currentFileIndex].albumArt}
              alt="Album Art"
              className="w-full h-64 object-cover rounded-lg mb-4"
            />
            <audio controls className="w-full" autoPlay>
              <source src={selectedFiles[currentFileIndex].url} type="audio/mpeg" />
              Your browser does not support the audio element.
            </audio>
          </>
        )}
      </div>
    </div>
  );
};

export default MusicPlayer;
